var button=document.querySelector(".button-find");var popup=document.querySelector(".form-unshow");var form=document.querySelector("form");var text=popup.querySelector("[name=date-arrival]");var number=popup.querySelector("[name=number]");document.addEventListener("DOMContentLoaded",function(){form.classList.add("form-show")});button.addEventListener("click",function(a){a.preventDefault();popup.classList.toggle("animation-form");text.focus()});window.addEventListener("keydown",function(a){if(a.keyCode===27){if(popup.classList.contains("form-show")){a.preventDefault();popup.classList.remove("form-unshow")}}});button.addEventListener("click",function(a){a.preventDefault();popup.classList.remove("modal-show");popup.classList.remove("modal-error")});form.addEventListener("submit",function(a){if(!text.value||!number.value){a.preventDefault();popup.classList.remove("modal-error");popup.offsetWidth=popup.offsetWidth;popup.classList.add("modal-error")}else{if(isStorageSupport){localStorage.setItem("text",text.value)}}});
